import React, {useState} from 'react';

const App = ()=>{
  
  const purple="#8e44ad";
  const [bg,setBg]=useState(purple);
  const [name ,setName]=useState('click me');
  const bgChange=()=>{
    //console.log("clicked");
    let newbg='#34495e';
    setBg(newbg);
    setName("Ouch!! dheeraj");
   
  };

  const bgBack=()=>{
    setBg(purple);
    setName("Ayyo!! Angry ");
  }

  return (

    <> 
      <div style={{backgroundColor:bg}}>
        <button onClick={bgChange} onDoubleClick={bgBack}>{name} Me</button>
      </div>
    </> 

  );
}

export default App;
