import React,{useState} from 'react';

const App=()=>{

  const [name,setName]=useState("");

  const InputEvent=(e)=>{
    console.log(e.target.value);
    setName(e.target.value);
    
  }

  return(
  <>
    <div>
      <h1> Hello {name}</h1>
      <input type="text"  placeholder="enter your name" 
        onChange={InputEvent}
      />
      <button>Click Me </button>
    </div>
  </>

)

}

export default App;
