import React,{useState} from 'react';

const App = ()=> {

  let newTime = new Date().toLocaleTimeString();
  const [cTime, setCTime]=useState(newTime);


  const UpdateTime = ( )=>{
    let newCTime = new Date().toLocaleTimeString();
    setCTime(newCTime);
    

  }

  return(
    <>
      <h1>{cTime}</h1>
      <button onClick={UpdateTime}> get Time </button>
    </>

  );

}

export default  App;
